CREATE or REPLACE VIEW `proj_inf` AS 
select 
   `prg`.`id` AS `id`,`prg`.`ver` AS `ver`,`prg`.`regras` AS `regras`,`prg`.`titulo` AS `titulo`,`prg`.`tipo_ext` AS `tipo_ext`,`prg`.`tipo_exten` AS `tipo_exten`,`prg`.`nome_prof` AS `nome_prof`,`prg`.`id_prof` AS `id_prof`,`prg`.`campus` AS `campus`,`prg`.`centros` AS `centros`,`prg`.`colegiado` AS `colegiado`,`prg`.`tide` AS `tide`,`prg`.`vigen_ini` AS `vigen_ini`,`prg`.`vigen_fim` AS `vigen_fim`,`prg`.`ch_semanal` AS `ch_semanal`,`prg`.`ch_total` AS `ch_total`,`prg`.`situacao` AS `situacao`,`prg`.`area_cnpq` AS `area_cnpq`,`prg`.`area_tema1` AS `area_tema1`,`prg`.`area_tema2` AS `area_tema2`,`prg`.`cnpq_garea` AS `cnpq_garea`,`prg`.`cnpq_area` AS `cnpq_area`,`prg`.`cnpq_sarea` AS `cnpq_sarea`,`prg`.`area_extensao` AS `area_extensao`,`prg`.`linh_ext` AS `linh_ext`,`prg`.`resumo` AS `resumo`,`prg`.`descricao` AS `descricao`,`prg`.`objetivos` AS `objetivos`,`prg`.`metodologia` AS `metodologia`,`prg`.`justificativa` AS `justificativa`,`prg`.`cronograma` AS `cronograma`,`prg`.`referencia` AS `referencia`,`prg`.`prodserv_espe` AS `prodserv_espe`,`prg`.`contribuicao` AS `contribuicao`,`prg`.`contrap_nofinac` AS `contrap_nofinac`,`prg`.`n_cert_prev` AS `n_cert_prev`,`prg`.`data` AS `data`,`prg`.`outs_info` AS `outs_info`,`prg`.`para_avaliar` AS `para_avaliar`,`prg`.`edt` AS `edt`,`prg`.`last_result` AS `last_result`,`prg`.`updated_at` AS `updated_at`,`prg`.`created_at` AS `created_at` 
   
from (
      select `prj`.`id` AS `id`,`prj`.`ver` AS `ver`,`prj`.`regras` AS `regras`,`prj`.`titulo` AS `titulo`,`prj`.`tipo_exten` AS `tipo_ext`,`tp`.`nome` AS `tipo_exten`,`prj`.`nome_prof` AS `nome_prof`,`prj`.`id_prof` AS `id_prof`,`ccc`.`campus` AS `campus`,`ccc`.`centros` AS `centros`,`ccc`.`colegiado` AS `colegiado`,`prj`.`tide` AS `tide`,`prj`.`vigen_ini` AS `vigen_ini`,`prj`.`vigen_fim` AS `vigen_fim`,`prj`.`ch_semanal` AS `ch_semanal`,`prj`.`ch_total` AS `ch_total`,`prj`.`situacao` AS `situacao`,`ac`.`nome` AS `area_cnpq`,`at1`.`nome` AS `area_tema1`,`at2`.`nome` AS `area_tema2`,`cnpq_ga`.`nome` AS `cnpq_garea`,`cnpq_ar`.`nome` AS `cnpq_area`,`cnpq_sa`.`nome` AS `cnpq_sarea`,`ae1`.`nome` AS `area_extensao`,`ae2`.`nome` AS `linh_ext`,`prj`.`resumo` AS `resumo`,`prj`.`descricao` AS `descricao`,`prj`.`objetivos` AS `objetivos`,`prj`.`metodologia` AS `metodologia`,`prj`.`justificativa` AS `justificativa`,`prj`.`cronograma` AS `cronograma`,`prj`.`referencia` AS `referencia`,`prj`.`prodserv_espe` AS `prodserv_espe`,`prj`.`contribuicao` AS `contribuicao`,`prj`.`contrap_nofinac` AS `contrap_nofinac`,`prj`.`n_cert_prev` AS `n_cert_prev`,`prj`.`data` AS `data`,`prj`.`outs_info` AS `outs_info`,`prj`.`para_avaliar` AS `para_avaliar`,`prj`.`edt` AS `edt`,`prj`.`last_result` AS `last_result`,`prj`.`updated_at` AS `updated_at`,`prj`.`created_at` AS `created_at` from ((((((((((`unespar_sistema`.`projetos` `prj` left join `unespar_sistema`.`ca_ce_co` `ccc` on((`prj`.`para_avaliar` = `ccc`.`co_id`))) left join `unespar_sistema`.`tipo_exten` `tp` on((`prj`.`tipo_exten` = `tp`.`id`))) left join `unespar_sistema`.`area_cnpq` `ac` on((`prj`.`area_cnpq` = `ac`.`id`))) left join `unespar_sistema`.`area_tematica` `at1` on((`prj`.`area_tema1` = `at1`.`id`))) left join `unespar_sistema`.`area_tematica` `at2` on((`prj`.`area_tema2` = `at2`.`id`))) left join `unespar_sistema`.`area_extensao` `ae1` on((`prj`.`area_extensao` = `ae1`.`id`))) left join `unespar_sistema`.`area_extensao` `ae2` on((`prj`.`area_extensao` = `ae2`.`id`))) left join `unespar_sistema`.`cnpq_garea` `cnpq_ga` on((`prj`.`cnpq_garea` = `cnpq_ga`.`id`))) left join `unespar_sistema`.`cnpq_area` `cnpq_ar` on((`prj`.`cnpq_area` = `cnpq_ar`.`id`))) left join `unespar_sistema`.`cnpq_subarea` `cnpq_sa` on((`prj`.`cnpq_sarea` = `cnpq_sa`.`id`)))) `prg`
      
